@WebServlet("/video-frames-sampler")
public class VideoFramesSamplerServlet extends HttpServlet {
    private static final long serialVersionUID = 1L;
    private SecureRandom random = new SecureRandom();</diff>

    protected void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        processRequest(request, response);
    }

    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        processRequest(request, response);
    }

    private void processRequest(HttpServletRequest request, HttpServletResponse response) throws IOException {
        String framesParam = request.getParameter("frames");
        String probabilityParam = request.getParameter("probability");

        if (framesParam == null || framesParam.isEmpty()) {
            sendWarningResponse(response, framesParam);
            return;
        }

        List<String> frames = parseFrames(framesParam);
        float probability = parseProbability(probabilityParam);

        List<String> sampledFrames = sampleFrames(frames, probability);

        sendSampledFramesResponse(response, sampledFrames);
    }

    private void sendWarningResponse(HttpServletResponse response, String framesParam) throws IOException {
        response.setContentType("text/html");
        PrintWriter out = response.getWriter();
        out.println("<html><body>");
        out.println("<p>Warning: No frames provided in the input: " + framesParam + "</p>");
        out.println("</body></html>");
    }

    private List<String> parseFrames(String framesParam) {
        String[] frameArray = framesParam.split(",");
        List<String> frames = new ArrayList<>();
        for (String frame : frameArray) {
            frames.add(frame.trim());
        }
        return frames;
    }

    private float parseProbability(String probabilityParam) {
        try {
            return Float.parseFloat(probabilityParam);
        } catch (NumberFormatException e) {
            return 0.0f;
        }
    }

    private List<String> sampleFrames(List<String> frames, float probability) {
        List<String> sampledFrames = new ArrayList<>();
        for (String frame : frames) {
            if (random.nextFloat() < probability) {
                sampledFrames.add(frame);
            }
        }
        return sampledFrames;
    }

    private void sendSampledFramesResponse(HttpServletResponse response, List<String> sampledFrames) throws IOException {
        response.setContentType("text/html");
        PrintWriter out = response.getWriter();
        out.println("<html><body>");
        out.println("<ul>");
        for (String frame : sampledFrames) {
            out.println("<li>" + frame + "</li>");
        }
        out.println("</ul>");
        out.println("</body></html>");
    }
}
