public class VectorSearchServlet extends HttpServlet {

    private static final int VECTOR_COUNT = 100000;
    private static final int VECTOR_DIMENSION = 10;
    private double[][] vectors;

    public VectorSearchServlet() {
        vectors = new double[VECTOR_COUNT][VECTOR_DIMENSION];
        Random random = new Random();</diff>
        for (int i = 0; i < VECTOR_COUNT; i++) {
            for (int j = 0; j < VECTOR_DIMENSION; j++) {
                vectors[i][j] = random.nextDouble() * 100;
            }
        }
    }

    @Override
    protected void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {
        ObjectMapper objectMapper = new ObjectMapper();
        RequestData requestData = objectMapper.readValue(request.getReader(), RequestData.class);

        List<String> matchingVectors = new ArrayList<>();
        for (int i = 0; i < vectors.length; i++) {
            double distance = euclideanDistance(requestData.getTarget(), vectors[i]);
            if (distance <= requestData.getThreshold()) {
                matchingVectors.add("Vector " + i + ": " + formatVector(vectors[i]));
            }
        }

        response.setContentType("text/html");
        PrintWriter out = response.getWriter();
        out.println("<html><body>");
        if (matchingVectors.isEmpty()) {
            out.println("No vectors found within distance " + requestData.getThreshold() + " of " + formatVector(requestData.getTarget()));
        } else {
            for (String vector : matchingVectors) {
                out.println(vector + "<br>");
            }
        }
        out.println("</body></html>");
    }

    private double euclideanDistance(double[] v1, double[] v2) {
        double sum = 0.0;
        for (int i = 0; i < v1.length; i++) {
            sum += Math.pow(v1[i] - v2[i], 2);
        }
        return Math.sqrt(sum);
    }

    private String formatVector(double[] vector) {
        StringBuilder sb = new StringBuilder();
        for (int i = 0; i < vector.length; i++) {
            sb.append(vector[i]);
            if (i < vector.length - 1) {
                sb.append(",");
            }
        }
        return sb.toString();
    }

    static class RequestData {
        private double[] target;
        private double threshold;

        public double[] getTarget() {
            return target;
        }

        public double getThreshold() {
            return threshold;
        }
    }
}
