    private final List<Integer> isotopeMasses;
    private final SecureRandom random;

    public ChemicalElement(Document document) {
        isotopeAbundances = new HashMap<>();
        random = new SecureRandom();</diff>

        Element root = document.getDocumentElement();
        NodeList isotopeNodes = root.getElementsByTagName("isotope");

        for (int i = 0; i < isotopeNodes.getLength(); i++) {
            Element isotope = (Element) isotopeNodes.item(i);
            int mass = Integer.parseInt(isotope.getAttribute("mass"));
            double abundance = Double.parseDouble(isotope.getAttribute("abundance"));
            isotopeAbundances.put(mass, abundance);
        }

        isotopeMasses = new ArrayList<>(isotopeAbundances.keySet());
        Collections.sort(isotopeMasses);
    }

    public int getRandomIsotope() {
        double rand = random.nextDouble();
        double cumulativeProbability = 0.0;

        for (Map.Entry<Integer, Double> entry : isotopeAbundances.entrySet()) {
