public class TrajectoryGenerator {

    public static List<Tuple> generateTrajectories(InputStream xmlConfig, int trajectoryLength) throws Exception {
        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        DocumentBuilder builder = factory.newDocumentBuilder();
        Document doc = builder.parse(xmlConfig);
        doc.getDocumentElement().normalize();

        Map<String, String> policy = parsePolicy(doc);
        Map<String, Map<String, Double>> transitionProbabilities = parseTransitions(doc);
        Map<String, Double> rewards = parseRewards(doc);

        JexlEngine jexl = new JexlBuilder().create();
        SecureRandom rand = new SecureRandom();</diff>

        List<Tuple> trajectories = new ArrayList<>();
        String currentState = "s1";

        for (int i = 0; i < trajectoryLength; i++) {
            String action = policy.get(currentState);
            double randomValue = rand.nextDouble();
            String nextState = getNextState(currentState, transitionProbabilities, randomValue, jexl);
            double reward = getReward(currentState, action, rewards);

            trajectories.add(new Tuple(currentState, action, nextState, reward));
            currentState = nextState;
        }

        return trajectories;
    }

    private static Map<String, String> parsePolicy(Document doc) {
        Map<String, String> policy = new HashMap<>();
        NodeList states = doc.getElementsByTagName("policy").item(0).getChildNodes();
        for (int i = 0; i < states.getLength(); i++) {
            Node stateNode = states.item(i);
            if (stateNode.getNodeType() == Node.ELEMENT_NODE) {
                Element stateElement = (Element) stateNode;
                String stateId = stateElement.getAttribute("id");
                String action = stateElement.getTextContent();
                policy.put(stateId, action);
            }
        }
        return policy;
    }

    private static Map<String, Map<String, Double>> parseTransitions(Document doc) throws Exception {
        Map<String, Map<String, Double>> transitions = new HashMap<>();
        NodeList fromStates = doc.getElementsByTagName("fromState");
        JexlEngine jexl = new JexlBuilder().create();

        for (int i = 0; i < fromStates.getLength(); i++) {
            Element fromStateElement = (Element) fromStates.item(i);
            String fromStateId = fromStateElement.getAttribute("id");
            Map<String, Double> toStates = new HashMap<>();

            NodeList toStateNodes = fromStateElement.getElementsByTagName("toState");
            for (int j = 0; j < toStateNodes.getLength(); j++) {
                Element toStateElement = (Element) toStateNodes.item(j);
                String toStateId = toStateElement.getAttribute("id");
                String probabilityExpr = toStateElement.getAttribute("probability");

                JexlExpression probabilityExp = jexl.createExpression(probabilityExpr);
                JexlContext context = new MapContext();
                double probability = (Double) probabilityExp.evaluate(context);

                toStates.put(toStateId, probability);
            }
            transitions.put(fromStateId, toStates);
        }
        return transitions;
    }

    private static Map<String, Double> parseRewards(Document doc) {
        Map<String, Double> rewards = new HashMap<>();
        NodeList rewardNodes = doc.getElementsByTagName("stateActionPair");

        for (int i = 0; i < rewardNodes.getLength(); i++) {
            Element rewardElement = (Element) rewardNodes.item(i);
            String state = rewardElement.getAttribute("state");
            String action = rewardElement.getAttribute("action");
            String rewardValue = rewardElement.getAttribute("value");

            rewards.put(state + "_" + action, Double.parseDouble(rewardValue));
        }
        return rewards;
    }

    private static String getNextState(String currentState, Map<String, Map<String, Double>> transitionProbabilities, double randomValue, JexlEngine jexl) throws Exception {
        Map<String, Double> toStates = transitionProbabilities.get(currentState);
        double cumulativeProbability = 0.0;

        for (Map.Entry<String, Double> entry : toStates.entrySet()) {
            cumulativeProbability += entry.getValue();
            if (randomValue < cumulativeProbability) {
                return entry.getKey();
            }
        }
        throw new Exception("No valid next state found for state: " + currentState);
    }

    private static double getReward(String currentState, String action, Map<String, Double> rewards) {
        return rewards.getOrDefault(currentState + "_" + action, 0.0);
    }

    public static class Tuple {
        public final String state;
        public final String action;
        public final String nextState;
        public final double reward;

        public Tuple(String state, String action, String nextState, double reward) {
            this.state = state;
            this.action = action;
            this.nextState = nextState;
            this.reward = reward;
        }

        @Override
        public String toString() {
            return "(" + state + ", " + action + ", " + nextState + ", " + reward + ")";
        }
