import org.w3c.dom.NodeList;
import java.io.InputStream;

public class BSTBuilder {

    public TreeNode buildBST(InputStream xmlInput) throws Exception {
        DocumentBuilderFactory dbFactory = DocumentBuilderFactory.newInstance();
        dbFactory.setFeature("http:
        dbFactory.setFeature("http:
<diff>        dbFactory.setXIncludeAware(false);
        dbFactory.setExpandEntityReferences(false);</diff>

        DocumentBuilder dBuilder = dbFactory.newDocumentBuilder();
        Document doc = dBuilder.parse(xmlInput);
        doc.getDocumentElement().normalize();

        Element treeRoot = doc.getDocumentElement();
        if (!treeRoot.getNodeName().equals("tree")) {
            throw new IllegalArgumentException("Root element must be <tree>");
        }

        TreeNode bstRoot = null;
        NodeList nodeList = treeRoot.getElementsByTagName("node");

        for (int i = 0; i < nodeList.getLength(); i++) {
            Node node = nodeList.item(i);
            if (node.getNodeType() == Node.ELEMENT_NODE) {
                Element element = (Element) node;
                int value = Integer.parseInt(element.getAttribute("value"));
                bstRoot = insert(bstRoot, value);
            }
        }

        return bstRoot;
    }

    private TreeNode insert(TreeNode root, int value) {
        if (root == null) {
            return new TreeNode(value);
        }
        if (value < root.val) {
            root.left = insert(root.left, value);
        } else if (value > root.val) {
            root.right = insert(root.right, value);
        }

        return root;
    }

    static class TreeNode {
        int val;
        TreeNode left;
        TreeNode right;
        TreeNode(int x) { val = x; }
    }
