public class ReverseLabelSmoothingLoss {

    public static float[] getReverseLabelSmoothingLoss(float[][] prediction, int[] label, String reduction) {
        float epsilon = readEpsilonFromConfig();
        int batchSize = prediction.length;
        int numClasses = prediction[0].length;
        float[] loss = new float[batchSize];

        for (int i = 0; i < batchSize; i++) {
            int trueClass = label[i];
            float trueClassProb = prediction[i][trueClass];
            float sumOtherClassesProb = 0.0f;

            for (int j = 0; j < numClasses; j++) {
                if (j != trueClass) {
                    sumOtherClassesProb += prediction[i][j];
                }
            }

            float trueClassLoss = (1 - epsilon) * -Math.log(trueClassProb);
            float otherClassesLoss = epsilon * -Math.log(sumOtherClassesProb / (numClasses - 1));

            loss[i] = trueClassLoss + otherClassesLoss;
        }

        if (reduction.equals("mean")) {
            float meanLoss = 0.0f;
            for (float l : loss) {
                meanLoss += l;
            }
            return new float[]{meanLoss / batchSize};
        } else if (reduction.equals("sum")) {
            float sumLoss = 0.0f;
            for (float l : loss) {
                sumLoss += l;
            }
            return new float[]{sumLoss};
        } else if (reduction.equals("none")) {
            return loss;
        } else {
            throw new IllegalArgumentException("Unsupported reduction type: " + reduction);
        }
    }

    private static float readEpsilonFromConfig() {
