    public static String serializeToJson(Object obj) throws Exception {
        String jsonString;

        if (isPrimitiveOrWrapper(obj.getClass()) || obj instanceof String) {
            jsonString = objectMapper.writeValueAsString(obj);
        } else {
            jsonString = serializeCustomObject(obj);
        }


