public class PathNavigator {

    public static List<Object> getAttributeValues(List<Object> objects, String path) {

        try {
<diff>
            ProcessBuilder processBuilder = new ProcessBuilder("pathcheck", path);
            Process process = processBuilder.start();</diff>
            int exitCode = process.waitFor();
            if (exitCode != 0) {

                return null;
            }
        } catch (Exception e) {

            return null;
        }


        String[] pathComponents = path.split("\\.");


        List<Object> attributeValues = new ArrayList<>();


        for (Object obj : objects) {
            Object current = obj;
            for (String component : pathComponents) {
                try {

                    Field field = current.getClass().getDeclaredField(component);
                    field.setAccessible(true);
                    current = field.get(current);
                } catch (NoSuchFieldException | IllegalAccessException e) {

                    attributeValues.add(null);
                    break;
                }
            }

            if (current != obj) {
                attributeValues.add(current);
            }
        }

        return attributeValues;
    }
}
