            System.out.println("Usage: java TextFileProcessor <filename> <separators>");
            System.exit(1);
        }

        String filename = args[0];
        String separators = String.join("|", Arrays.copyOfRange(args, 1, args.length));

        try {
            List<String> result = processFile(filename, separators);
            System.out.println(Arrays.toString(result.toArray()));
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

