import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Random;

public class ScreenRectangleGenerator {

    public static String generateValidScreenRectangle() throws IOException, InterruptedException {
        int screenWidth = getScreenWidth();
        int screenHeight = getScreenHeight();

        Random random = new Random();
        int x1, y1, x2, y2;

        do {
            x1 = random.nextInt(screenWidth);
            y1 = random.nextInt(screenHeight);
            x2 = random.nextInt(screenWidth);
            y2 = random.nextInt(screenHeight);
        } while (x2 <= x1 || y2 <= y1);

        return x1 + "," + y1 + "," + x2 + "," + y2;
    }

    private static int getScreenWidth() throws IOException, InterruptedException {
        return getScreenResolution()[0];
    }

    private static int getScreenHeight() throws IOException, InterruptedException {
        return getScreenResolution()[1];
    }

    private static int[] getScreenResolution() throws IOException, InterruptedException {
        Process process;
        if (System.getProperty("os.name").toLowerCase().contains("win")) {
<diff>            process = new ProcessBuilder("systeminfo").start();
        } else {
            process = new ProcessBuilder("xrandr").start();</diff>
        }

        BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
        String line;
        int width = 0, height = 0;
        while ((line = reader.readLine()) != null) {
            if (System.getProperty("os.name").toLowerCase().contains("win")) {
                if (line.contains("Screen")) {
                    String[] parts = line.split(":\\s+");
                    if (parts.length > 1) {
                        String resolutionPart = parts[1].trim().split(" ")[0];
                        String[] dimensions = resolutionPart.split("x");
                        if (dimensions.length == 2) {
                            width = Integer.parseInt(dimensions[0]);
                            height = Integer.parseInt(dimensions[1]);
                            break;
                        }
                    }
                }
            } else {
                if (line.contains(" connected")) {
                    String[] parts = line.split("\\s+");
                    if (parts.length > 3) {
