import java.util.Arrays;

public class SaltManager {
    private static final String SALT_FILE = "salt.txt";
    private static final String CHECKSUM_COMMAND = "sha256sum";

    public static byte[] generateAndStoreSalt() throws IOException {
        byte[] salt = new byte[16];
        SecureRandom secureRandom = new SecureRandom();
        secureRandom.nextBytes(salt);

        try (FileOutputStream fos = new FileOutputStream(SALT_FILE)) {
            fos.write(salt);
        }

        return salt;
    }

    public static byte[] retrieveSalt() throws IOException {
        String expectedChecksum = computeChecksum(SALT_FILE);
        String actualChecksum = executeChecksumCommand(SALT_FILE);

        if (!expectedChecksum.equals(actualChecksum)) {
            throw new IOException("Checksum mismatch, file integrity compromised.");
        }

        try (FileInputStream fis = new FileInputStream(SALT_FILE)) {
            byte[] salt = new byte[16];
            int bytesRead = fis.read(salt);
            if (bytesRead != 16) {
                throw new IOException("Unexpected salt length.");
            }
            return salt;
        }
    }

    private static String computeChecksum(String filename) throws IOException {
        byte[] fileBytes = Files.readAllBytes(new File(filename).toPath());
