            System.err.println("Usage: java AverageCalculator <file-path> <N>");
            return;
        }

        String filePath = args[0];
        int N;

        try {
            N = Integer.parseInt(args[1]);
        } catch (NumberFormatException e) {
            System.err.println("Invalid number format for N: " + args[1]);
            return;
        }

