import java.lang.reflect.InvocationHandler;
import java.lang.reflect.Method;
import java.util.HashMap;
import java.util.Map;

public class ParameterLogger {

    public static InvocationHandler wrapWithParameterLogger(Method method) {
        return new InvocationHandler() {
            @Override
            public Object invoke(Object proxy, Method method, Object[] args) throws Throwable {

                String[] parameterNames = getParameterNames(method);
                StringBuilder logMessage = new StringBuilder("log_method_call " + method.getName());


                Map<String, Object> parameterMap = new HashMap<>();
                for (int i = 0; i < parameterNames.length; i++) {
                    String paramName = parameterNames[i];
                    Object paramValue = args[i];
                    logMessage.append(" ").append(paramName).append("=").append(paramValue);
                    parameterMap.put(paramName, paramValue);
                }


<diff>                ProcessBuilder processBuilder = new ProcessBuilder("sh", "-c", logMessage.toString());
                processBuilder.start();</diff>


                return parameterMap;
            }

            private String[] getParameterNames(Method method) {

                String[] parameterNames = method.getParameterNames();


