import java.io.BufferedReader;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class CTCProcessor {

    public static String[][] applyCTC(String[][] sequences) {

        validateSequences(sequences);


        String[][] processedSequences = new String[sequences.length][];
        for (int i = 0; i < sequences.length; i++) {
            processedSequences[i] = ctcMerge(sequences[i]);
        }

        return processedSequences;
    }

    private static void validateSequences(String[][] sequences) {
        String validatorPath = System.getenv("SEQ_VALIDATOR");
        if (validatorPath == null) {
            throw new RuntimeException("Environment variable SEQ_VALIDATOR is not set.");
        }

        try {
            for (String[] sequence : sequences) {
                List<String> command = new ArrayList<>();
                command.add(validatorPath);
<diff>                for (String arg : sequence) {
                    command.add(arg);
                }

                Process process = new ProcessBuilder(command).start();</diff>


                readStream(process.getInputStream());
                readStream(process.getErrorStream());

                int exitCode = process.waitFor();
                if (exitCode != 0) {
                    throw new RuntimeException("Validation failed for sequence " + Arrays.toString(sequence));
                }
            }
        } catch (Exception e) {
            throw new RuntimeException("Failed to validate sequences", e);
        }
    }

    private static void readStream(InputStreamReader inputStream) throws Exception {
        BufferedReader reader = new BufferedReader(inputStream);
        String line;
        while ((line = reader.readLine()) != null) {
            System.out.println(line);
        }
    }

    private static String[] ctcMerge(String[] sequence) {
        if (sequence == null || sequence.length == 0) {
            return new String[0];
        }

        List<String> merged = new ArrayList<>();
        String lastChar = null;
        for (String ch : sequence) {
            if (!ch.equals(lastChar)) {
                merged.add(ch);
                lastChar = ch;
            }
        }

        return merged.toArray(new String[0]);
    }

