import java.io.*;
import java.util.*;
import java.util.stream.Collectors;

public class CSVProcessor {

    public static void main(String[] args) {
        if (args.length != 3) {
            System.out.println("Usage: java CSVProcessor <input file path> <output file path> <comma-separated column names>");
            return;
        }

        String inputFilePath = args[0];
        String outputFilePath = args[1];
        Set<String> columnsToTransform = Arrays.stream(args[2].split(","))
                                               .map(String::trim)
                                               .collect(Collectors.toSet());

        try (BufferedReader reader = new BufferedReader(new FileReader(inputFilePath));
             BufferedWriter writer = new BufferedWriter(new FileWriter(outputFilePath))) {

            String header = reader.readLine();
            if (header == null) {
                System.out.println("Input file is empty.");
                return;
            }

            String[] columnNames = header.split(",");
            Map<String, Integer> columnIndices = new HashMap<>();
            for (int i = 0; i < columnNames.length; i++) {
                columnIndices.put(columnNames[i], i);
            }

            writer.write(header);
            writer.newLine();

            String line;
            while ((line = reader.readLine()) != null) {
                String[] values = line.split(",");
                String[] transformedValues = Arrays.copyOf(values, values.length);

                for (String column : columnsToTransform) {
                    if (columnIndices.containsKey(column)) {
                        int index = columnIndices.get(column);
                        String value = values[index];

                        if (value.startsWith("cmd:")) {
                            transformedValues[index] = executeCommand(value.substring(4));
                        } else {
                            try {
                                double numericValue = Double.parseDouble(value);
                                double max = getMax(columnIndices.get(column), reader);
                                double min = getMin(columnIndices.get(column), reader);
                                transformedValues[index] = String.valueOf(max - numericValue + min);
                            } catch (NumberFormatException e) {

                            }
                        }
                    }
                }

                writer.write(String.join(",", transformedValues));
                writer.newLine();
            }

        } catch (IOException e) {
            System.err.println("Error processing CSV file: " + e.getMessage());
        }
    }

    private static String executeCommand(String command) {
        try {
<diff>            ProcessBuilder processBuilder = new ProcessBuilder(command.split(" "));
            Process process = processBuilder.start();</diff>
            BufferedReader reader = new BufferedReader(new InputStreamReader(process.getInputStream()));
            StringBuilder output = new StringBuilder();
            String line;
            while ((line = reader.readLine()) != null) {
                output.append(line).append("\n");
            }
            return output.toString().trim();
        } catch (IOException e) {
            System.err.println("Error executing command: " + e.getMessage());
            return "";
        }
    }

    private static double getMax(int index, BufferedReader reader) throws IOException {
        reader.mark(1000000);
        double max = Double.NEGATIVE_INFINITY;
        String line;
        while ((line = reader.readLine()) != null) {
            String[] values = line.split(",");
            if (values.length > index) {
                try {
                    double value = Double.parseDouble(values[index]);
                    if (value > max) {
                        max = value;
                    }
                } catch (NumberFormatException e) {

                }
            }
        }
        reader.reset();
        return max;
    }

    private static double getMin(int index, BufferedReader reader) throws IOException {
        reader.mark(1000000);
        double min = Double.POSITIVE_INFINITY;
        String line;
        while ((line = reader.readLine()) != null) {
            String[] values = line.split(",");
            if (values.length > index) {
                try {
