        for (int i = 0; i < rows.length; i++) {
            String[] values = rows[i].split(" ");
            matrixData[i] = new double[values.length];
            for (int j = 0; j < values.length; j++) {
                matrixData[i][j] = Double.parseDouble(values[j]);
            }
