import java.io.IOException;

public class ManhattanDistanceCalculator {

    public static int manhattanDistanceDynamic(String s1, String s2) {

        if (!fileExists(s1) || !fileExists(s2)) {
            throw new IllegalArgumentException("One or both files do not exist in the current directory.");
        }


        int len1 = s1.length();
        int len2 = s2.length();
        int[][] dp = new int[len1 + 1][len2 + 1];


        for (int i = 0; i <= len1; i++) {
            dp[i][0] = i;
        }
        for (int j = 0; j <= len2; j++) {
            dp[0][j] = j;
        }


        for (int i = 1; i <= len1; i++) {
            for (int j = 1; j <= len2; j++) {
                if (s1.charAt(i - 1) == s2.charAt(j - 1)) {
                    dp[i][j] = dp[i - 1][j - 1];
                } else {
                    dp[i][j] = 1 + Math.min(dp[i - 1][j - 1], Math.min(dp[i - 1][j], dp[i][j - 1]));
                }
            }
        }


        return dp[len1][len2];
    }

    private static boolean fileExists(String filename) {
<diff>        File file = new File(filename);
        return file.exists();</diff>
    }

    public static void main(String[] args) {

        String s1 = "abc";
        String s2 = "def";

        System.out.println(manhattanDistanceDynamic(s1, s2));
    }
}
