        List<Double> infectionProportions = new ArrayList<>();

        for (int timestep = 0; timestep < numTimesteps; timestep++) {
            int infectedCount = 0;


            List<Individual> toInfect = new ArrayList<>();


            for (Individual individual : individuals) {
                if (individual.isInfected()) {
                    infectedCount++;
                    for (Individual neighbor : individual.getNeighbors()) {
                        if (!neighbor.isInfected() && Math.random() < pInfection) {
                            toInfect.add(neighbor);
                        }
                    }
                }
            }


            for (Individual individual : toInfect) {
                individual.setInfected(true);
            }


            double infectionProportion = (double) infectedCount / individuals.size();
            infectionProportions.add(infectionProportion);


            logInfectionStatus(timestep, infectionProportion, logFileName);
        }

        return infectionProportions;
    }

