public class ColorSorter {
    private ArrayList<int[]> colors;

    public ColorSorter(ArrayList<int[]> colors) {
        this.colors = colors;
    }

    public void sortColors() {
        if (colors == null || colors.size() < 2) {
            return;
        }
        mergeSort(colors, 0, colors.size() - 1);
    }

    private void mergeSort(ArrayList<int[]> colors, int left, int right) {
        if (left < right) {
            int mid = (left + right) / 2;
            mergeSort(colors, left, mid);
            mergeSort(colors, mid + 1, right);
            merge(colors, left, mid, right);
            logColors();
        }
    }

    private void merge(ArrayList<int[]> colors, int left, int mid, int right) {
        int n1 = mid - left + 1;
        int n2 = right - mid;

        List<int[]> leftArr = new ArrayList<>(n1);
        List<int[]> rightArr = new ArrayList<>(n2);

        for (int i = 0; i < n1; ++i) {
            leftArr.add(colors.get(left + i));
        }
        for (int j = 0; j < n2; ++j) {
            rightArr.add(colors.get(mid + 1 + j));
        }

        int i = 0, j = 0, k = left;
        while (i < n1 && j < n2) {
