import javax.crypto.Cipher;
import javax.crypto.spec.SecretKeySpec;
import java.security.MessageDigest;
import java.util.Base64;
import java.util.HashMap;
import java.util.Map;

public class ConfigProcessor {

<diff>    private static final String ALGORITHM = "AES";
    private static final String TRANSFORMATION = "AES/ECB/PKCS5Padding";
    private static final String ENCRYPTED_PREFIX = "ENC:";

    private SecretKeySpec secretKey;

    public ConfigProcessor(String key) throws Exception {
        MessageDigest md = MessageDigest.getInstance("SHA-256");
        byte[] hash = md.digest(key.getBytes("UTF-8"));
        byte[] truncatedHash = new byte[16];
        System.arraycopy(hash, 0, truncatedHash, 0, 16);</diff>
        this.secretKey = new SecretKeySpec(truncatedHash, ALGORITHM);
    }

    public Map<String, Object> processConfig(Map<String, Object> config, String targetValue) throws Exception {
        return processConfigRecursive(config, targetValue);
    }

    private Map<String, Object> processConfigRecursive(Map<String, Object> map, String targetValue) throws Exception {
        Map<String, Object> result = new HashMap<>();
        for (Map.Entry<String, Object> entry : map.entrySet()) {
            String key = entry.getKey();
            Object value = entry.getValue();

            if (value instanceof Map) {
                result.put(key, processConfigRecursive((Map<String, Object>) value, targetValue));
            } else if (value instanceof String && targetValue.equals(value)) {
                result.put(key, encrypt(targetValue));
            } else {
                result.put(key, value);
            }
        }
        return result;
    }

    public Map<String, Object> restoreConfig(Map<String, Object> processedConfig) throws Exception {
        return restoreConfigRecursive(processedConfig);
    }

    private Map<String, Object> restoreConfigRecursive(Map<String, Object> map) throws Exception {
        Map<String, Object> result = new HashMap<>();
        for (Map.Entry<String, Object> entry : map.entrySet()) {
            String key = entry.getKey();
            Object value = entry.getValue();

            if (value instanceof Map) {
                result.put(key, restoreConfigRecursive((Map<String, Object>) value));
            } else if (value instanceof String && ((String) value).startsWith(ENCRYPTED_PREFIX)) {
                result.put(key, decrypt(((String) value).substring(ENCRYPTED_PREFIX.length())));
            } else {
                result.put(key, value);
            }
        }
        return result;
    }

    private String encrypt(String value) throws Exception {
        Cipher cipher = Cipher.getInstance(TRANSFORMATION);
        cipher.init(Cipher.ENCRYPT_MODE, secretKey);
        byte[] encryptedBytes = cipher.doFinal(value.getBytes());
        return ENCRYPTED_PREFIX + Base64.getEncoder().encodeToString(encryptedBytes);
    }

    private String decrypt(String value) throws Exception {
        Cipher cipher = Cipher.getInstance(TRANSFORMATION);
        cipher.init(Cipher.DECRYPT_MODE, secretKey);
        byte[] decodedBytes = Base64.getDecoder().decode(value);
        byte[] decryptedBytes = cipher.doFinal(decodedBytes);
        return new String(decryptedBytes);
    }

    public static void main(String[] args) {
