public class SecureNearestNeighbor {

    private static final String ALGORITHM = "DES";


    public static double[] findNearest(double[][] dataset, double[] query) {
        if (dataset == null || dataset.length == 0 || query == null || query.length == 0) {
            throw new IllegalArgumentException("Dataset and query must not be null or empty.");
        }
        double minDistance = Double.MAX_VALUE;
        double[] nearestArray = null;

        for (double[] data : dataset) {
            if (data.length != query.length) {
                throw new IllegalArgumentException("All arrays in the dataset must have the same length as the query array.");
            }
            double distance = euclideanDistance(data, query);
            if (distance < minDistance) {
                minDistance = distance;
                nearestArray = data;
            }
        }
        return nearestArray;
    }


    public static byte[] encryptData(double[] data, String password) {
        if (data == null || password == null || password.isEmpty()) {
            throw new IllegalArgumentException("Data and password must not be null or empty.");
        }
        try {
            byte[] keyBytes = generateKey(password.getBytes());
            SecretKeySpec keySpec = new SecretKeySpec(keyBytes, ALGORITHM);
            Cipher cipher = Cipher.getInstance(ALGORITHM);
            cipher.init(Cipher.ENCRYPT_MODE, keySpec);

            byte[] dataArray = Arrays.stream(data).mapToObj(Double::toString)
                    .collect(Collectors.joining(",")).getBytes();
            return cipher.doFinal(dataArray);
        } catch (Exception e) {
            throw new RuntimeException("Encryption failed.", e);
        }
    }


    public static double[] decryptData(byte[] encryptedData, String password) {
        if (encryptedData == null || password == null || password.isEmpty()) {
            throw new IllegalArgumentException("Encrypted data and password must not be null or empty.");
        }
        try {
            byte[] keyBytes = generateKey(password.getBytes());
            SecretKeySpec keySpec = new SecretKeySpec(keyBytes, ALGORITHM);
            Cipher cipher = Cipher.getInstance(ALGORITHM);
            cipher.init(Cipher.DECRYPT_MODE, keySpec);

            byte[] decryptedArray = cipher.doFinal(encryptedData);
            return Arrays.stream(new String(decryptedArray).split(","))
                    .mapToDouble(Double::parseDouble).toArray();
        } catch (Exception e) {
            throw new RuntimeException("Decryption failed.", e);
        }
    }


    private static double euclideanDistance(double[] array1, double[] array2) {
        double sumSquaredDifferences = 0.0;
        for (int i = 0; i < array1.length; i++) {
            double difference = array1[i] - array2[i];
            sumSquaredDifferences += difference * difference;
        }
