<diff>import java.security.SecureRandom;
import java.util.List;
import java.util.Map;

class Node {
    String type;
    List<Node> children;

    Node(String type, List<Node> children) {
        this.type = type;
        this.children = children;
    }
}

public class TreeValidator {

    private final SecureRandom random = new SecureRandom();</diff>
    private final double sampleRate = 0.1;

    public boolean validateTree(Node root, Map<String, List<String>> schema) {
        return validateNode(root, schema);
    }

    private boolean validateNode(Node node, Map<String, List<String>> schema) {
        if (node == null) {
            return true;
        }


        if (shouldSample(node)) {
            if (!isValidNode(node, schema)) {
                return false;
            }
            for (Node child : node.children) {
                if (!isValidChild(node, child, schema)) {
                    return false;
                }
            }
        }


        for (Node child : node.children) {
            if (!validateNode(child, schema)) {
                return false;
            }
        }

        return true;
    }

    private boolean shouldSample(Node node) {

        if (node == null) {
            return false;
        }
        return random.nextDouble() < sampleRate;
    }

    private boolean isValidNode(Node node, Map<String, List<String>> schema) {
        return schema.containsKey(node.type);
    }

    private boolean isValidChild(Node parent, Node child, Map<String, List<String>> schema) {
        List<String> allowedChildTypes = schema.get(parent.type);
        return allowedChildTypes != null && allowedChildTypes.contains(child.type);
    }
}
