    private static SecureRandom random = new SecureRandom();</diff>

    public static void main(String[] args) {
        float[][] predictions = generateTestData(5, 10);
        float[] targets = predictions[random.nextInt(predictions.length)];
        int k = 3;
        float recall = recallAtK(predictions, targets, k);
        System.out.println("Recall@" + k + ": " + recall);
    }

    public static float recallAtK(float[][] predictions, float[] targets, int k) {
        if (predictions.length != targets.length) {
            throw new IllegalArgumentException("Predictions and targets must have the same length.");
        }

        int totalRelevantItems = 0;
        int relevantItemsInTopK = 0;

        for (int i = 0; i < predictions.length; i++) {
            float[] prediction = predictions[i];
            float target = targets[i];

            if (target != 0) {
                totalRelevantItems++;
                int[] topKIndices = getTopKIndices(prediction, k);
                for (int index : topKIndices) {
                    if (prediction[index] == target) {
                        relevantItemsInTopK++;
                        break;
                    }
                }
            }
        }

        if (totalRelevantItems == 0) {
            return 0;
        }

        return (float) relevantItemsInTopK / totalRelevantItems;
    }

    private static int[] getTopKIndices(float[] array, int k) {
        int[] indices = new int[array.length];
        for (int i = 0; i < array.length; i++) {
            indices[i] = i;
        }


        for (int i = 0; i < k; i++) {
            for (int j = i + 1; j < array.length; j++) {
                if (array[indices[i]] < array[indices[j]]) {
                    int temp = indices[i];
                    indices[i] = indices[j];
                    indices[j] = temp;
                }
            }
        }


        return java.util.Arrays.copyOf(indices, k);
    }

    public static float[][] generateTestData(int rows, int cols) {
        float[][] data = new float[rows][cols];
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                data[i][j] = random.nextFloat() * 100;
            }
        }


        for (int i = 0; i < rows; i++) {
            int targetIndex = random.nextInt(cols);
            data[i][targetIndex] = 100;
        }

        return data;
    }
}
