        if (numbers.isEmpty()) {
            return 0.0;
        }

        SecureRandom random = new SecureRandom();</diff>
        List<Double> weightedValues = new ArrayList<>(numbers.size());


        for (Double number : numbers) {
            double weight = 0.5 + random.nextDouble();
            double weightedValue = number * weight;
