            for (double value : weightedValues) {
                sum += value;
            }
            return sum / size;
        } else {

            Collections.sort(weightedValues);
            return weightedValues.get(size / 2);
        }
    }

    public static void main(String[] args) {

        List<Double> result1 = Arrays.asList(1.0, 2.0, 3.0, 4.0);
