<diff>import java.security.SecureRandom;

public class TensorAnalyzer {

    private SecureRandom random;

    public TensorAnalyzer() {
        this.random = new SecureRandom();</diff>
    }

    public double[][][] generateRandomTensor(int size) {
        double[][][] tensor = new double[size][size][size];
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < size; j++) {
                for (int k = 0; k < size; k++) {
                    tensor[i][j][k] = random.nextDouble();
                }
            }
        }
        return tensor;
    }

    public double calculateDensity(double[][][] tensor) {
        int nonZeroCount = 0;
        int totalElements = 0;

        for (double[][] matrix : tensor) {
            for (double[] row : matrix) {
                for (double value : row) {
                    totalElements++;
                    if (value != 0) {
                        nonZeroCount++;
                    }
                }
            }
        }

        return (double) nonZeroCount / totalElements;
    }

    public static void main(String[] args) {
        TensorAnalyzer analyzer = new TensorAnalyzer();
        double[][][] randomTensor = analyzer.generateRandomTensor(10);
        double density = analyzer.calculateDensity(randomTensor);
        System.out.println(density);
    }
}
