public class KLDivergenceEstimator {

    private static SecureRandom random = new SecureRandom();</diff>

    public static double estimateKLDivergence(double[] meanP, double[][] covP, double[] meanQ, double[][] covQ) {
        int numSamples = 10000;
        int dim = meanP.length;

        double klDivergence = 0.0;

        for (int i = 0; i < numSamples; i++) {
            double[] sample = sampleMultivariateNormal(meanP, covP);
            double logProbP = logMultivariateNormalDensity(sample, meanP, covP);
            double logProbQ = logMultivariateNormalDensity(sample, meanQ, covQ);

            klDivergence += logProbP - logProbQ;
        }

        return klDivergence / numSamples;
    }

    private static double[] sampleMultivariateNormal(double[] mean, double[][] cov) {
        int dim = mean.length;
        double[] z = new double[dim];
        for (int i = 0; i < dim; i++) {
            z[i] = random.nextGaussian();
        }

        double[] result = new double[dim];
        double[][] chol = choleskyDecomposition(cov);
        for (int i = 0; i < dim; i++) {
            for (int j = 0; j <= i; j++) {
                result[i] += chol[i][j] * z[j];
            }
            result[i] += mean[i];
        }
        return result;
    }

    private static double logMultivariateNormalDensity(double[] x, double[] mean, double[][] cov) {
        int dim = mean.length;
        double[] diff = new double[dim];
        for (int i = 0; i < dim; i++) {
            diff[i] = x[i] - mean[i];
