        double normA = 0.0;
        double normB = 0.0;

        for (int i = 0; i < A.length; i++) {
            dotProduct += A[i] * B[i];
            normA += A[i] * A[i];
            normB += B[i] * B[i];
        }

        double cosineSimilarity = dotProduct / (Math.sqrt(normA) * Math.sqrt(normB));
        return 1.0 - cosineSimilarity;
