public class IniParser {

    public static Map<String, Map<String, String>> parseIniFile(String filePath) throws IOException {
        Map<String, Map<String, String>> configMap = new HashMap<>();
        BufferedReader reader = null;
        String currentSection = null;

        try {
            reader = new BufferedReader(new FileReader(filePath));
            String line;

            while ((line = reader.readLine()) != null) {
                line = line.trim();

                if (line.isEmpty() || line.startsWith(";") || line.startsWith("#")) {
                    continue;
                }

                if (line.startsWith("[") && line.endsWith("]")) {

                    currentSection = line.substring(1, line.length() - 1).trim();
                    configMap.put(currentSection, new HashMap<>());
                } else if (currentSection != null) {

                    int equalIndex = line.indexOf('=');
                    if (equalIndex != -1) {
                        String key = line.substring(0, equalIndex).trim();
                        String value = line.substring(equalIndex + 1).trim();
                        configMap.get(currentSection).put(key, value);
                    }
                }
            }
        } finally {
            if (reader != null) {
                reader.close();
            }
        }


        for (String section : configMap.keySet()) {
            int trackingId = generateTrackingId(section);
            configMap.get(section).put("tracking_id", String.format("%04d", trackingId));
        }

        return configMap;
    }

    private static int generateTrackingId(String sectionName) {
        MessageDigest md;
        try {
<diff>            md = MessageDigest.getInstance("SHA-256");
        } catch (NoSuchAlgorithmException e) {
            throw new RuntimeException("SHA-256 algorithm not found", e);</diff>
        }
        byte[] hashBytes = md.digest(sectionName.getBytes());
        int hash = Math.abs(Arrays.hashCode(hashBytes)) % 9000 + 1000;
        return hash;
    }

