            int action = chooseAction(currentState, epsilon);


            int nextState = getNextState(currentState, action);
            double reward = getReward(nextState);


            updatePolicy(currentState, action, reward, nextState);


            currentState = nextState;


            done = isEpisodeDone(currentState);
        }
    }


    private int chooseAction(int state, double epsilon) {
