import java.io.ByteArrayInputStream;
import java.io.ObjectInputStream;
import java.io.IOException;
import java.util.Scanner;

public class DeserializeAndFilterString {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        System.out.println("Enter the serialized string data:");


        String serializedData = scanner.nextLine();

        try {

            byte[] serializedBytes = hexStringToByteArray(serializedData);


            String deserializedString = deserializeString(serializedBytes);


            String filteredString = deserializedString.replaceAll("[^a-zA-Z0-9]", "");


            System.out.println("Filtered string: " + filteredString);
        } catch (IOException | ClassNotFoundException e) {
            e.printStackTrace();
        }
    }

    private static byte[] hexStringToByteArray(String s) {
        int len = s.length();
        byte[] data = new byte[len / 2];
        for (int i = 0; i < len; i += 2) {
            data[i / 2] = (byte) ((Character.digit(s.charAt(i), 16) << 4)
                                 + Character.digit(s.charAt(i+1), 16));
        }
