import java.io.*;
import java.util.*;

class Point implements Serializable {
    double x, y, z;
    long timestamp;

    public Point(double x, double y, double z, long timestamp) {
        this.x = x;
        this.y = y;
        this.z = z;
        this.timestamp = timestamp;
    }
}

public class TDOACalculator {

    public Map<String, Long> computeTDOA(byte[] serializedData) throws IOException, ClassNotFoundException {

        List<Point> points = deserializePoints(serializedData);


        Map<String, Long> tdoaMap = new HashMap<>();


        for (int i = 0; i < points.size(); i++) {
            for (int j = i + 1; j < points.size(); j++) {
                long tdoa = Math.abs(points.get(i).timestamp - points.get(j).timestamp);
                tdoaMap.put(i + "-" + j, tdoa);
            }
        }

        return tdoaMap;
