import javax.servlet.http.HttpServletResponse;

@WebServlet("/generate-matrix")
public class MatrixGeneratorServlet extends HttpServlet {

    private static final SecureRandom RANDOM = new SecureRandom();</diff>

    protected void doPost(HttpServletRequest request, HttpServletResponse response)
            throws ServletException, IOException {


        String rowsParam = request.getParameter("rows");
        String colsParam = request.getParameter("cols");
        String minParam = request.getParameter("min");
        String maxParam = request.getParameter("max");


        int rows = parsePositiveInteger(rowsParam, 1);
        int cols = parsePositiveInteger(colsParam, 1);
        float min = parseFloat(minParam, -100f);
        float max = parseFloat(maxParam, 100f);


        float[][] matrix = generateRandomMatrix(rows, cols, min, max);


        response.setContentType("text/html;charset=UTF-8");
        PrintWriter out = response.getWriter();
        try {
            out.println("<!DOCTYPE html>");
            out.println("<html lang='en'>");
            out.println("<head>");
            out.println("<meta charset='UTF-8'>");
            out.println("<title>Generated Matrix</title>");
            out.println("</head>");
            out.println("<body>");
            out.println("<h1>Generated Matrix</h1>");
            out.println("<table border='1'>");
            for (float[] row : matrix) {
                out.println("<tr>");
                for (float value : row) {
                    out.printf("<td>%.2f</td>", value);
                }
                out.println("</tr>");
            }
            out.println("</table>");
            out.println("</body>");
            out.println("</html>");
        } finally {
            out.close();
        }
    }

    private int parsePositiveInteger(String param, int defaultValue) {
        try {
            int value = Integer.parseInt(param);
            return Math.max(1, value);
        } catch (NumberFormatException e) {
            return defaultValue;
        }
    }

    private float parseFloat(String param, float defaultValue) {
        try {
            return Float.parseFloat(param);
        } catch (NumberFormatException e) {
            return defaultValue;
        }
    }

    private float[][] generateRandomMatrix(int rows, int cols, float min, float max) {
        float[][] matrix = new float[rows][cols];
        for (int i = 0; i < rows; i++) {
            for (int j = 0; j < cols; j++) {
                matrix[i][j] = min + (max - min) * RANDOM.nextFloat();
            }
        }
        return matrix;
    }
}
