import hashlib

def list_subdirectories_with_files(directory_path):

    List all subdirectories that contain files and return a list of dictionaries
<diff>    with the directory name and SHA-256 checksums of all files within it.

    if not os.path.exists(directory_path):
        raise FileNotFoundError(f"Directory '{directory_path}' does not exist.")

    result = []

    for root, dirs, files in os.walk(directory_path):
        if files:
            checksums = {}
            for file in files:
                file_path = os.path.join(root, file)
                try:
                    with open(file_path, 'rb') as f:
                        file_data = f.read()
                        sha256_checksum = hashlib.sha256(file_data).hexdigest()
                        checksums[file] = sha256_checksum</diff>
                except Exception as e:
                    print(f"Error processing file {file_path}: {e}")
            result.append({'dirname': os.path.relpath(root, directory_path), 'checksums': checksums})

    return result
